import { useEffect, useState } from "react";

function useAllproducts(){
    const [ allproducts , setAllproducts ] = useState([]);
    useEffect(()=>{

        fetch('http://localhost:3000/allproducts/')
        .then((result)=>result.json())
        .then((data)=>{
            setAllproducts(data); 
           // console.log(`printed in hook ${JSON.stringify(data)}`)
        })
    },[])

    return allproducts; 
}

function useOneproducts(_id){
    const [ oneproducts , setOneproducts ] = useState([]);
    useEffect(()=>{

        fetch(`http://localhost:3000/product/${_id}`)
        .then((result)=>result.json())
        .then((data)=>{
            setOneproducts(data); 
           // console.log(`printed in hook ${JSON.stringify(data)}`)
        })
    },[])

    return oneproducts; 
}

function Products(){
const  Data = useAllproducts(); 
    return(
    <div>
       {Data.map((product,index)=> (
                <Oneproduct product={product} key={index}></Oneproduct>
            ))}
    </div> 
        )

} 
function Oneproduct(props){
return(
<div style={{color:'blueviolet',background:'red',border:'2px solid black'}}>
{props.product.name}<br></br>
{props.product._id}<br></br>
{props.product.description}<br></br>
{props.product.price}<br></br>
{props.product.sellingPrice}<br></br>
<button onClick={()=>{console.log(`{props.product.name}`)}}></button>
</div>
)
}

export default Products;
